/* Table for therapist */
CREATE TABLE THERAPIST (
[Therapist ID] int identity(1,1),
[First Name] nvarchar(20) NOT NULL,
[Last Name] nvarchar(20) NOT NULL,
Street nvarchar(50) NULL,
City nvarchar(20) NULL,
[State] nvarchar(15) NULL,
[Date of Birth] date NOT NULL,
[Zip] int NOT NULL,
[E-mail] nvarchar(30) NULL,
[cell] nvarchar(15) NULL,
[Hired on] date NOT NULL

constraint therapistID_PK PRIMARY KEY([Therapist ID])
)

/* table for patient */
CREATE TABLE PATIENT (
[Patient ID] int identity(1,1),
[First Name] nvarchar(20) NOT NULL,
[Last Name] nvarchar(20) NOT NULL,
Street nvarchar(50) NOT NULL,
City nvarchar(20) NOT NULL,
[State] nvarchar(15) NOT NULL,
[Zip] int NOT NULL,
[Date of Birth] date NOT NULL,
[E-mail] nvarchar(30) NOT NULL,
[Cell #] nvarchar(15) NULL,

constraint patientID_PK PRIMARY KEY([Patient ID])


)

/* insurance authorization table */
CREATE TABLE [INS_AUTHORIZATION] (
[Patient ID] int,
[Authorization #] nvarchar(15),
[Number of Visits] int NULL,
[Eff. Start Date] date,
[Eff. End Date] date

constraint patientAuth_FK FOREIGN KEY([Patient ID]) REFERENCES PATIENT
ON DELETE CASCADE
constraint patientAUTH_PK PRIMARY KEY([Patient ID], [Authorization #])
)

/* insurance table */
CREATE TABLE INSURANCE (
[Patient ID] int, 
[Policy Number] nvarchar(10),
[Group Number] nvarchar(10),
[Group Name] nvarchar(20) NOT NULL,
Deductible int NULL


constraint patientINS_PK PRIMARY KEY([Patient ID], [Policy Number], [Group Number]),
constraint patientINS_FK FOREIGN KEY([Patient ID]) REFERENCES PATIENT
ON DELETE CASCADE

)

/* service table */
CREATE TABLE [SERVICE] (
[Service Name] nvarchar(40),
[Service Desc.] nvarchar(255) NOT NULL

constraint serviceName_PK PRIMARY KEY([Service Name])
)

/* Appointment table */
CREATE TABLE APPOINTMENT (
[Patient ID] int,
[Therapist ID] int,
[Date] date,
[Start Time] time,
[End Time] time NOT NULL,
[Service Name] nvarchar(40),
[Reason for Visit] nvarchar(255) NULL

constraint service_FK FOREIGN KEY([Service Name]) REFERENCES [SERVICE],
constraint patientApt_FK FOREIGN KEY([Patient ID]) REFERENCES PATIENT
ON DELETE CASCADE,
constraint therapistID_FK FOREIGN KEY([Therapist ID]) REFERENCES THERAPIST,
constraint composite_PK PRIMARY KEY([Patient ID], [Therapist ID], [Date], [Start Time])
)



GO
CREATE VIEW PATIENT_VIEW AS
SELECT PATIENT.[Patient ID], PATIENT.[First Name], PATIENT.[Last Name], PATIENT.Street, PATIENT.City, PATIENT.[State], PATIENT.Zip, PATIENT.[Date of Birth], PATIENT.[E-Mail], PATIENT.[Cell #],
INSURANCE.[Policy Number], INSURANCE.[Group Number], INSURANCE.[Group Name], INSURANCE.Deductible, INS_AUTHORIZATION.[Authorization #], INS_AUTHORIZATION.[Number of Visits],
INS_AUTHORIZATION.[Eff. Start Date], INS_AUTHORIZATION.[Eff. End Date]
FROM PATIENT LEFT OUTER JOIN INSURANCE ON PATIENT.[Patient ID] = INSURANCE.[Patient ID] LEFT OUTER JOIN INS_AUTHORIZATION ON INSURANCE.[Patient ID] = INS_AUTHORIZATION.[Patient ID]


